var myMap;

ymaps.ready(init);

function init () {

    myMap = new ymaps.Map('map', {
        center: [45.022698, 38.970112],
        controls: [ ],
        zoom: 13
    }, {
        searchControlProvider: 'yandex#search'
    });

    var myPlacemark = new ymaps.Placemark([45.022698, 38.970112], {}, {
        iconLayout: 'default#image',
        iconImageHref: 'img/icon2/point.svg',
        iconImageSize: [30, 42],
        iconImageOffset: [-15, -40]
    });

    myMap.geoObjects
        .add(myPlacemark);

// Добавим на карту ползунок масштаба и линейку.
    myMap.controls.add('zoomControl');

//Отключить изменение маштаба колесом и косанием
    myMap.behaviors.disable(['scrollZoom' , 'multiTouch' , 'drag']);
}
